import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

const handleInitialRedirect = () => {
  // Check if the app is launched as a standalone PWA
  if (window.matchMedia('(display-mode: standalone)').matches) {
    // Redirect within the PWA to the specified URL (replace with your desired URL)
    window.location.href = 'https://www.google.com'; // Example: 'https://www.google.com'
  }
};

// Call the function to handle the initial redirect
handleInitialRedirect();

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
